sparql.en:
# Get JCM microbial strains with the growth temperature larger than 55.0,
# and images of the strains if available 

PREFIX xsd: &lt;http://www.w3.org/2001/XMLSchema#&gt; 
PREFIX owl: &lt;http://www.w3.org/2002/07/owl#&gt;
PREFIX dct: &lt;http://purl.org/dc/terms/&gt;
PREFIX mccv: &lt;http://purl.jp/bio/01/mccv#&gt;

SELECT ?entry_id ?jcm ?strain_name ?temperature ?image
FROM &lt;http://metadb.riken.jp/metadb/db/rikenbrc_jcm_microbe&gt;
WHERE {
  ?jcm_entry &lt;http://metadb.riken.jp/db/brc_beta/brc_brc_0000037&gt; ?jcm_uri .
  ?jcm_entry mccv:MCCV_000010 ?entry_id .
  ?jcm_entry mccv:MCCV_000012 ?strain_name .
  ?jcm_entry mccv:MCCV_000014 ?bk .
  ?bk &lt;http://metadb.riken.jp/db/brc_beta/brc_0000054&gt; ?temperature .
  OPTIONAL {?jcm_entry &lt;http://xmlns.com/foaf/0.1/depiction&gt; ?image } .
  BIND(IRI(?jcm_uri) AS ?jcm)
  FILTER(?temperature &gt; 55.0)
} LIMIT 100

